INSERT INTO `tool_quesbank` VALUES ('2018秋季', '毛概', '/第五章/第二节', '多选题', '20世纪50年代中期，围绕台湾问题的国内外形势发生了很大的变化。根据形势的发展变化，党及时调整了对台政策，提出了和平解放台湾的主张，并从两个方面开展工作，主要是（）', 'AB', '', '敦促美国政府与中国政府谈判', '向台湾当局提出和平解决台湾的倡议', '向台湾当局提出武力解决台湾的倡议', '敦促美国政府与英国政府谈判', '', '', '', '', '', '');
