INSERT INTO `tool_quesbank` VALUES ('2018秋季', '毛概', '/第五章/第二节', '多选题', '1964年，周恩来在三届全国人大一次会议的政府工作报告中宣布：“从第三个五年计划开始，我国的国民经济发展，可以按两步来考虑：第一步，建立一个独立的比较完整的工业体系和国民经济体系；第二步，全面实现（）和科学技术的现代化，使我国经济走在世界的前列。”', 'ABC', '', '农业', '工业', '国防', '军事', '', '', '', '', '', '');
