INSERT INTO `tool_quesbank` VALUES ('2018秋季', '毛概', '/第七章/第一节', '单选题', '经过新中国成立以来特别是改革开放以来的不懈努力，我国经济社会发展取得了举世瞩目的成就，但仍处于并将长期处于社会主义（）阶段的基本国情没有变。', 'A', '', '初级', '中级', '高级', '特级', '', '', '', '', '', '');
